def histogram(s):  # Counts how many times each letter appears in a given string and stores counter in dictionary    d = dict()  # creates empty dictionary    for c in s:  # for every character in the string        if c not in d:  # if character is not already in the dictionary            d[c] = 1  # add to dicitonary, set counter at 1        else:            d[c] += 1  # incease counter by 1    return d